project.description:
Creates a package from a process and deploys the package to a given envirnoment
If package with the same version exists, a new package is not created. If the packageID is deployed, the deployment step is ignored

source bin/deployProcess.sh env=${env} processName=${processName} packageVersion=${packageVersion} createdBy=${createdBy} notes=${notes} listenerStatus=${listenerStatus}

hudson.tasks.Shell.command:
cd ${SCRIPTS_HOME}
cd ${SCRIPTS_HOME}
saveProcessName=${processName}
source bin/queryProcess.sh processName=${processName}
source bin/queryEnvironment.sh env=${env} classification="*"
toEnvId=$envId
deployProcessId=$processId
source bin/createProcessAttachment.sh envId=$envId processId=$processId componentType="Process"
source bin/deployProcess.sh processId=${deployProcessId} envId=${toEnvId} componentType="Process" notes="${notes}"
mkdir -p "${WORKSPACE}/${BUILD_NUMBER}/${saveProcessName}/${deployProcessId}"
cp /var/jenkins_home/boomi/Atom_${atomName}/processes/${deployProcessId}/${deployProcessId}.xml "${WORKSPACE}"/${BUILD_NUMBER}/${saveProcessName}/${deployProcessId}/*
for file in ""${WORKSPACE}"/${BUILD_NUMBER}/${saveProcessName}/${deployProcessId}/*.xml"; do tidy -m -i -xml $file; done

cd "${WORKSPACE}/${BUILD_NUMBER}"
git add ${saveProcessName}
git commit -m "Saving ${saveProcessName} file to GIT."
